    inner class CustomViewHolderSuccess(itemView: View) : RecyclerView.ViewHolder(itemView){
        val txt = itemView.findViewById<TextView>(R.id.textViewSuccessNumber)
    }

    override fun onCreateViewHolder(parent: ViewGroup, position: Int): CustomViewHolderSuccess {
        return CustomViewHolderSuccess( LayoutInflater.from(parent.context).inflate(R.layout.recyclersuccess, parent, false))
    }

    override fun getItemCount(): Int {
        return successAdvice.count()
    }

    override fun onBindViewHolder(parent: CustomViewHolderSuccess, position: Int) {
        parent.txt.text = successAdvice[position].name
        parent.txt.setOnClickListener {
            val myIntent = Intent(context,SuccessDetailActivity::class.java)
            myIntent.putExtra("text",successDescription[position].desc)
            context.startActivity(myIntent)
        }
    }
}